import React from "react";
import "./home.css";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import ClearIcon from "@material-ui/icons/Clear";
import CreationForm from "./CreationForm/creationForm";
import Wrapper from "./Wrapper";
import Tickets from "./TicketListings/tickets";

class Home extends React.Component {
  state = {
    creationForm: false,
    roleName: false,
  };
  componentDidMount() {
    let user = JSON.parse(localStorage.getItem("userData"));
    if (user != null) {

        if (user.authorities.length == 1 && user.authorities[0].roleName == "COURIER") {
          this.setState({ roleName: false });
        } else {
          this.setState({ roleName: true });
        }

    }
  }
  render() {
    return (
      <div>
        <Wrapper>
          {this.state.roleName ?    <Fab
            style={{ float: "right" }}
            color="secondary"
            aria-label="add"
            onClick={() => {
              this.setState({ creationForm: !this.state.creationForm });
            }}
          >
            {!this.state.creationForm ? <AddIcon /> : <ClearIcon />}
          </Fab> : null}

          {this.state.creationForm ? <CreationForm /> : <Tickets />}
        </Wrapper>
      </div>
    );
  }
}

export default Home;
