import React, { Component } from "react";
import Wrapper from "../Wrapper";
import axios from "axios";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import DateFnsUtils from "@date-io/date-fns";
import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from "@material-ui/pickers";

class Raports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tickets: [],
      checkedB: false,
      selectedDate:""
    };
    this.handleChange = this.handleChange.bind(this);
  }
  componentDidMount() {
    axios
      .get("/ticket")
      .then((response) => {
        console.log(response.data);
        this.setState({ tickets: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  handleChange = (event) => {
    this.setState({ ...this.state, [event.target.name]: event.target.checked });
  };
  handleDateChange = (date) => {
    this.setState({selectedDate:date})
  };
  render() {
    const { tickets, checkedB,selectedDate } = this.state;
    return (
      <Wrapper>
        <div>
          <Grid container spacing={4}>
            <Grid item xs={12} sm={6}>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={checkedB}
                    onChange={this.handleChange}
                    name="checkedB"
                    color="primary"
                  />
                }
                label="Data krijimit"
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <KeyboardDatePicker
                disableToolbar
                variant="inline"
                format="MM/dd/yyyy"
                margin="normal"
                id="date-picker-inline"
                label="Date picker inline"
                value={selectedDate}
                onChange={this.handleDateChange}
                KeyboardButtonProps={{
                  "aria-label": "change date",
                }}
              />
            </Grid>
          </Grid>
        </div>

        <div>
          <TableContainer component={Paper} style={{ marginTop: "20px" }}>
            <Table aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell>NO</TableCell>
                  <TableCell>State</TableCell>
                  <TableCell align="right">First name</TableCell>
                  <TableCell align="right">Last name</TableCell>
                  <TableCell align="right">Mobile</TableCell>
                  <TableCell align="right">Comment</TableCell>
                  <TableCell align="right">Address</TableCell>
                  <TableCell align="right">City</TableCell>
                  <TableCell align="right">State</TableCell>
                  <TableCell align="right">Price</TableCell>
                  <TableCell align="right">Curreny</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tickets?.length
                  ? tickets.map((ticket) => (
                      <TableRow
                        key={ticket.id}
                        onClick={() => this.handleClickOpen(ticket)}
                        style={{ cursor: "pointer" }}
                      >
                        <TableCell component="th" scope="row">
                          {new Date(ticket.date).toLocaleDateString("en-US")}{" "}
                          {new Date(ticket.date).toLocaleTimeString("en-US")}
                        </TableCell>
                        <TableCell align="right">{ticket.no}</TableCell>
                        <TableCell
                          align="right"
                          onClick={(e) => {
                            this.changeStatus(e, ticket);
                          }}
                        >
                          {" "}
                          <Chip
                            label={
                              ticket.ticketProgress[
                                ticket.ticketProgress.length - 1
                              ].cargoState.description
                            }
                            variant="default"
                            style={{
                              backgroundColor:
                                ticket.ticketProgress[
                                  ticket.ticketProgress.length - 1
                                ].cargoState.state === "CREATED"
                                  ? "#c0bdc1e3"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "STORED"
                                  ? "#ff7100"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "DISTRIBUTING"
                                  ? "#4959bf"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "DELIVERED"
                                  ? "#00bf40"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "REJECTED"
                                  ? "#ff2424"
                                  : "",
                            }}
                          />
                        </TableCell>
                        <TableCell align="right">{ticket.firstName}</TableCell>
                        <TableCell align="right">{ticket.lastName}</TableCell>
                        <TableCell align="right">{ticket.mobile}</TableCell>
                        <TableCell align="right">{ticket.comment}</TableCell>
                        <TableCell align="right">{ticket.address}</TableCell>
                        <TableCell align="right">{ticket.city.city}</TableCell>
                        <TableCell align="right">{ticket.state}</TableCell>
                        <TableCell align="right">{ticket.price}</TableCell>
                        <TableCell align="right">
                          {ticket.currency.currency}
                        </TableCell>
                      </TableRow>
                    ))
                  : "No data"}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </Wrapper>
    );
  }
}

export default Raports;
