import React, { Component } from "react";
import Wrapper from "../Wrapper";
import Button from "@material-ui/core/Button";
import axios from "axios";
import ReactToPrint from "react-to-print";
import ComponentToBePrinted from "./componentToBePrinted";

class PrintedForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ticket: {},
    };
    console.log(this.props);
    if (this.props.history.location.state != null) {
      let query = this.props.history.location.state.ticket;
      axios
        .get("/ticket/ticket", {
          params: {
            id: query,
          },
        })
        .then((response) => {
          console.log(response.data);
          this.setState({ ticket: response.data });
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }
  componentDidMount() {}

  render() {
    const { ticket } = this.state;
    return (
      <div>
        <Wrapper>

          {this.state.ticket != {} ? (
            <div style={{ float: "right"}}>
              {" "}
              <ReactToPrint

                trigger={() => {
                  return (
                    <Button variant="contained" color="secondary">
                      Print
                    </Button>
                  );
                }}
                content={() => this.componentRef}
              />
              <ComponentToBePrinted
                data={this.state.ticket}
                ref={(el) => (this.componentRef = el)}
              />{" "}
            </div>
          ) : null}
        </Wrapper>
      </div>
    );
  }
}

export default PrintedForm;
