import React, { Component } from "react";
import Wrapper from "../Wrapper";
import axios from "axios";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import { withStyles } from "@material-ui/styles";
import { withRouter } from "react-router-dom";
import DateFnsUtils from "@date-io/date-fns";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import InputLabel from "@material-ui/core/InputLabel";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import RefreshIcon from '@material-ui/icons/Refresh';

import {
  MuiPickersUtilsProvider,
  KeyboardTimePicker,
  KeyboardDatePicker,
} from "@material-ui/pickers";


const styles = (theme) => ({
    table: {
      minWidth: 1000,
    },
  });
class Raports extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tickets: [],
      selectedFromCreatedDate: null,
      selectedToCreatedDate: null,
      selectedFromDeliveryDate: null,
      selectedToDeliveryDate: null,
      selectedFromAcceptedDate: null,
      selectedToAcceptedDate: null,
      selectedFromPaymentDate: null,
      selectedToPaymentDate: null,
      selectedDate: null,
      cargoStates: [],
      status: "",
      customer: "",
    };

    this.filterTicketData = this.filterTicketData.bind(this);
    this.cleanFilters = this.cleanFilters.bind(this);
  }

  convertDate(date){

  }
  getCargoStates() {
    axios
      .get("/cargoState")
      .then((response) => {
        console.log(response);
        this.setState({ cargoStates: response.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }


  getTickets(){
    axios
    .get("/ticket")
    .then((response) => {
      console.log(response.data);
      this.setState({ tickets: response.data });
    })
    .catch((error) => {
      console.log(error);
    });

  }
  componentDidMount() {
    this.getCargoStates();
    this.getTickets();

  }

  handleCreatedFromDateChange = (date) => {
    this.setState({
      selectedFromCreatedDate: date,
    });
  };
  handleCreatedToDateChange = (date) => {
    this.setState({
      selectedToCreatedDate: date,
    });
  };
  handleDeliveryFromDateChange = (date) => {
    this.setState({
      selectedFromDeliveryDate: date,
    });
  };
  handleDeliveryToDateChange = (date) => {
    this.setState({
      selectedToDeliveryDate: date,
    });
  };
  handleAcceptedFromDateChange = (date) => {
    this.setState({
      selectedFromAcceptedDate: date,
    });
  };
  handleAcceptedToDateChange = (date) => {
    this.setState({
      selectedToAcceptedDate: date,
    });
  };
  handlePaymentFromDateChange = (date) => {
    this.setState({
      selectedFromPaymentDate: date,
    });
  };
  handlePaymentToDateChange = (date) => {
    this.setState({
      selectedToPaymentDate: date,
    });
  };
  handleStatusChange = (event) => {
    this.setState({ status: event.target.value });
  };
  _handleTextFieldChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };
  cleanFilters(){
    this.state.selectedFromCreatedDate = null;
    this.state.selectedToCreatedDate = null;
    this.state.selectedFromDeliveryDate = null;
    this.state.selectedToDeliveryDate=null;
    this.state.selectedFromAcceptedDate= null;
    this.state.selectedToAcceptedDate= null;
    this.state.selectedFromPaymentDate= null;
    this.state.selectedToPaymentDate= null;
    this.state.status="";
    this.state.customer= "";
    this.getTickets();
  }
  filterTicketData() {
    let body = {
      ticketStatus: this.state.status.state,
      customer: this.state.customer,
      creationStartDate: this.state.selectedFromCreatedDate != null ? new Date(this.state.selectedFromCreatedDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      creationEndDate: this.state.selectedToCreatedDate != null ? new Date(this.state.selectedToCreatedDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      acceptedStartDate: this.state.selectedFromAcceptedDate != null ? new Date(this.state.selectedFromAcceptedDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      acceptedEndDate: this.state.selectedToAcceptedDate != null ? new Date(this.state.selectedToAcceptedDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      deliveredStartDate: this.state.selectedFromDeliveryDate != null ? new Date(this.state.selectedFromDeliveryDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      deliveredEndDate: this.state.selectedToDeliveryDate != null ? new Date(this.state.selectedToDeliveryDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      liquidatedStartDate: this.state.selectedFromPaymentDate != null ? new Date(this.state.selectedFromPaymentDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
      liquidatedEndDate: this.state.selectedToPaymentDate != null ? new Date(this.state.selectedToPaymentDate).toISOString("yyyy-MM-dd'T'HH:mm:ss.SSSSSS") : null,
    };
    console.log(body)
    axios
    .post("/report", body, { "Content-Type": "application/json" })
    .then((response) => {
     console.log(response)
     this.setState({tickets:response.data});
    })
    .catch((error) => {
      console.log("login error", error);
    });
  }
  render() {
    const {
      tickets,
      selectedFromCreatedDate,
      selectedToCreatedDate,
      selectedFromDeliveryDate,
      selectedToDeliveryDate,
      selectedFromAcceptedDate,
      selectedToAcceptedDate,
      selectedFromPaymentDate,
      selectedToPaymentDate,
      selectedDate,
      cargoStates,
      status,
      customer,
    } = this.state;
    const { classes } = this.props;
    return (
      <Wrapper>
        <div>
          <Grid container spacing={2}>
            <Grid item xs={4} sm={4}  md={4} style={{ marginTop: "40px" }}>
              <InputLabel id="demo-simple-select-label">Status</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                value={status}
                label="Status"
                onChange={this.handleStatusChange}
                style={{ width: "100%" }}
              >
                {cargoStates.map((state) => (
                  <MenuItem key={state.id} value={state}>
                    {state.description}
                  </MenuItem>
                ))}
              </Select>
            </Grid>
            <Grid item xs={4} sm={4} md={4} style={{ marginTop: "30px" }}>
              <TextField
                style={{ padding: "10px", width: "100%" }}
                id="standard-secondary"
                label="Customer"
                name="customer"
                value={customer}
                onChange={this._handleTextFieldChange}
                color="secondary"
              />
            </Grid>
          </Grid>

          <Grid container spacing={2}>
            <Grid item xs={12} sm={2} md={2}>
              <div style={{padding: "39px 30px"}}>Data e krijimit</div>
            </Grid>
            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Nga data"
                  value={selectedFromCreatedDate}
                  onChange={this.handleCreatedFromDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>
            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Deri ne daten"
                  value={selectedToCreatedDate}
                  onChange={this.handleCreatedToDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={12} sm={2} md={2}>
              <div style={{padding: "39px 30px"}}> Data e dorezimit</div>
            </Grid>
            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Nga data"
                  value={selectedFromDeliveryDate}
                  onChange={this.handleDeliveryFromDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Deri ne daten"
                  value={selectedToDeliveryDate}
                  onChange={this.handleDeliveryToDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>
          </Grid>

          <Grid container spacing={2}>
            <Grid item xs={12} sm={2} md={2}>
              <div style={{padding: "39px 30px"}}>Data e pranimit</div>
            </Grid>
            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Nga data"
                  value={selectedFromAcceptedDate}
                  onChange={this.handleAcceptedFromDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Deri ne daten"
                  value={selectedToAcceptedDate}
                  onChange={this.handleAcceptedToDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={12} sm={2} md={2}>
              <div style={{padding: "39px 30px"}}>Data e pageses</div>
            </Grid>
            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Nga data"
                  value={selectedFromPaymentDate}
                  onChange={this.handlePaymentFromDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>

            <Grid item xs={6} sm={2} md={2}>
              <MuiPickersUtilsProvider utils={DateFnsUtils}>
                <KeyboardDatePicker
                  disableToolbar
                  variant="inline"
                  format="MM/dd/yyyy"
                  margin="normal"
                  id="date-picker-inline"
                  label="Deri ne daten"
                  value={selectedToPaymentDate}
                  onChange={this.handlePaymentToDateChange}
                  KeyboardButtonProps={{
                    "aria-label": "change date",
                  }}
                />
              </MuiPickersUtilsProvider>
            </Grid>
          </Grid>
          <Grid item xs={12} md={12} m={12} style={{ marginTop: "20px" }}>
            <Button
              variant="contained"
              color="secondary"
              onClick={this.filterTicketData}
              style={{ alignSelf: "center" }}
            >
              Filtro
            </Button>
          </Grid>
        </div>

        <div style={{ marginTop: "40px" }}>
            <div>
            <Button
              variant="contained"
              color="primary"
              onClick={this.cleanFilters}
              style={{ alignSelf: "center" }}
            >
              <RefreshIcon/> Pastro filtrat
            </Button>
            </div>
          <TableContainer component={Paper} >
            <Table className={classes.table} aria-label="simple table">
              <TableHead>
                <TableRow>
                  <TableCell>Date</TableCell>
                  <TableCell>NO</TableCell>
                  <TableCell>State</TableCell>
                  <TableCell align="right">First name</TableCell>
                  <TableCell align="right">Last name</TableCell>
                  <TableCell align="right">Mobile</TableCell>
                  <TableCell align="right">Comment</TableCell>
                  <TableCell align="right">Address</TableCell>
                  <TableCell align="right">City</TableCell>
                  <TableCell align="right">State</TableCell>
                  <TableCell align="right">Price</TableCell>
                  <TableCell align="right">Curreny</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {tickets?.length
                  ? tickets.map((ticket) => (
                      <TableRow
                        key={ticket.id}

                        style={{ cursor: "pointer" }}
                      >
                        <TableCell component="th" scope="row">
                          {new Date(ticket.date).toLocaleDateString("en-US")}{" "}
                          {new Date(ticket.date).toLocaleTimeString("en-US")}
                        </TableCell>
                        <TableCell align="right">{ticket.no}</TableCell>
                        <TableCell
                          align="right"
                          onClick={(e) => {
                            this.changeStatus(e, ticket);
                          }}
                        >
                          {" "}
                          <Chip
                            label={
                              ticket.ticketProgress[
                                ticket.ticketProgress.length - 1
                              ].cargoState.description
                            }
                            variant="default"
                            style={{
                              backgroundColor:
                                ticket.ticketProgress[
                                  ticket.ticketProgress.length - 1
                                ].cargoState.state === "CREATED"
                                  ? "#c0bdc1e3"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "STORED"
                                  ? "#ff7100"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "DISTRIBUTING"
                                  ? "#4959bf"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "DELIVERED"
                                  ? "#00bf40"
                                  : ticket.ticketProgress[
                                      ticket.ticketProgress.length - 1
                                    ].cargoState.state === "REJECTED"
                                  ? "#ff2424"
                                  : "",
                            }}
                          />
                        </TableCell>
                        <TableCell align="right">{ticket.firstName}</TableCell>
                        <TableCell align="right">{ticket.lastName}</TableCell>
                        <TableCell align="right">{ticket.mobile}</TableCell>
                        <TableCell align="right">{ticket.comment}</TableCell>
                        <TableCell align="right">{ticket.address}</TableCell>
                        <TableCell align="right">{ticket.city.city}</TableCell>
                        <TableCell align="right">{ticket.state}</TableCell>
                        <TableCell align="right">{ticket.price}</TableCell>
                        <TableCell align="right">
                          {ticket.currency.currency}
                        </TableCell>
                      </TableRow>
                    ))
                  : "No data"}
              </TableBody>
            </Table>
          </TableContainer>
        </div>
      </Wrapper>
    );
  }
}

export default withRouter(withStyles(styles)(Raports));
